(682, '_transient_berocket_1_paid_info', 'a:13:{s:2:\"id\";s:1:\"1\";s:4:\"name\";s:32:\"WooCommerce AJAX Products Filter\";s:4:\"slug\";s:32:\"woocommerce-ajax-products-filter\";s:10:\"plugin_url\";s:61:\"https://berocket.com/product/woocommerce-ajax-products-filter\";s:7:\"version\";s:7:\"3.1.1.6\";s:5:\"about\";s:113:\"Take a look at this fantastic AJAX products filter plugin for WooCommerce. Add unlimited filters with one widget.\";s:10:\"difference\";a:25:{i:0;s:33:\"Widget to filter by Modified Date\";i:1;s:56:\"Up to 4 colors for Attribute values in Type color Widget\";i:2;s:148:\"Custom Sidebar for filters <a href=\'https://woocommerce-products-filter.berocket.com/woocommerce-products-filter-demo-2/\' target=\'_blank\'>[demo]</a>\";i:3;s:133:\"Filters over products <a href=\'https://woocommerce-products-filter.berocket.com/product-category/tshirts/\' target=\'_blank\'>[demo]</a>\";i:4;s:135:\"Show Filter Titles only <a href=\'https://woocommerce-products-filter.berocket.com/product-category/tshirts/\' target=\'_blank\'>[demo]</a>\";i:5;s:148:\"Collapsed filters over products <a href=\'https://woocommerce-products-filter.berocket.com/product-category/all-products/\' target=\'_blank\'>[demo]</a>\";i:6;s:143:\"SEO Meta and Title <a href=\'https://woocommerce-products-filter.berocket.com/shop/filters/price/465_1913/color/red/\' target=\'_blank\'>[demo]</a>\";i:7;s:28:\"Filters by Custom Taxonomies\";i:8;s:22:\"Filter by Stock Status\";i:9;s:32:\"Filter by Product On Sale Status\";i:10;s:36:\"Remove/Leave only some filter values\";i:11;s:46:\"Checkbox/Values List for Price as Price Ranges\";i:12;s:45:\"Slider Range for Attributes/Custom Taxonomies\";i:13;s:31:\"Datepicker Style for Attributes\";i:14;s:39:\"Datepicker for Product Publication Date\";i:15;s:42:\"Display Product Count for Attribute Values\";i:16;s:34:\"Display 1-4 filter(s) per Line/Row\";i:17;s:80:\"Search Box Block (a group of filters that redirects to other pages on filtering)\";i:18;s:31:\"Nice URLs for SEO Friendly URLs\";i:19;s:28:\" Links Setup after filtering\";i:20;s:35:\"Generate Canonical Links for a page\";i:21;s:44:\"Add Filters to Page Title/Description/Header\";i:22;s:63:\"Display Variation Image/Price that matches the selected filters\";i:23;s:72:\"Open a product with Selected Variation that matches the selected filters\";i:24;s:24:\"Additional Filter Styles\";}s:7:\"related\";a:3:{i:0;s:1:\"3\";i:1;s:2:\"18\";i:2;s:2:\"21\";}s:5:\"image\";s:61:\"https://berocket.com/img/b15144f7012aa6e6badd844905dec34f.png\";s:10:\"mini_image\";s:61:\"https://berocket.com/img/fef38793de09251e3d7dd4520965cc16.png\";s:10:\"shop_image\";s:61:\"https://berocket.com/img/163ffebae875ac349d4430b2e388ca99.png\";s:5:\"price\";s:2:\"44\";s:7:\"buy_url\";s:34:\"https://berocket.com/add_to_cart/1\";}', 'no'),
